mapper.select:
select detail.id, detail.receipt_order_id, detail.receipt_order_type, detail.order_no, detail.`type`, detail.sku_id, detail.warehouse_id, detail.area_id, detail.quantity, detail.batch_no, detail.production_date, detail.expiration_date, detail.amount, detail.remark, detail.remain_quantity, detail.create_by, detail.create_time, detail.update_by, detail.update_time, detail.remain_gross_weight, detail.remain_net_weight, sku.id as sku_id, sku.*, ro.time, item.id as item_id, item.* from wms_inventory_detail detail left join wms_receipt_order ro on detail.receipt_order_id = ro.id inner join wms_item_sku sku on detail.sku_id=sku.id inner join wms_item item on sku.item_id=item.id
detail.batch_no like concat('%', #{bo.batchNo}, '%')
and item.item_name like concat('%', #{bo.itemName}, '%')
and item.item_code like concat('%', #{bo.itemCode}, '%')
and sku.sku_name like concat('%', #{bo.skuName}, '%')
and sku.sku_code like concat('%', #{bo.skuCode}, '%')
and item.id=#{bo.itemId}
and detail.warehouse_id=#{bo.warehouseId}
and detail.area_id=#{bo.areaId}
and detail.batch_no=#{bo.batchNo}
and (ro.time between #{bo.createStartTime} and #{bo.createEndTime})
and (detail.expiration_date between #{bo.expirationStartTime} and #{bo.expirationEndTime})
order by detail.warehouse_id,detail.area_id,sku.item_id,sku.id,ro.time

select
    detail.*
from wms_inventory_detail detail
         inner join wms_item_sku sku on detail.sku_id=sku.id


        detail.batch_no like concat('%', #{bo.batchNo}, '%')


        and detail.warehouse_id=#{bo.warehouseId}


        and detail.area_id=#{bo.areaId}


        and detail.batch_no=#{bo.batchNo}


order by sku.item_id,sku.id,detail.create_time desc
